    AND lastLoginTime = #{item.lastLoginTime,jdbcType=TIMESTAMP}




    AND lastLoginTime >= #{item.lastLoginTimeBegin,jdbcType=TIMESTAMP}




    AND lastLoginTime <= #{item.lastLoginTimeEnd,jdbcType=TIMESTAMP}





    AND mobilePhone LIKE #{item.mobilePhone,jdbcType=VARCHAR}





    AND userPortrait LIKE #{item.userPortrait,jdbcType=VARCHAR}




    AND createTime = #{item.createTime,jdbcType=TIMESTAMP}




    AND createTime >= #{item.createTimeBegin,jdbcType=TIMESTAMP}




    AND createTime <= #{item.createTimeEnd,jdbcType=TIMESTAMP}





    AND closeUserName LIKE #{item.closeUserName,jdbcType=VARCHAR}





  ORDER BY ${item.orderByParam}



    ${item.orderByAccording}






  LIMIT #{item.pageSize,jdbcType=INTEGER}
